import React, { useState, useEffect } from 'react';
import Nav from '../components/Nav';

const API = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      const titles = ['titanic', 'shutter island', 'inception', 'stranger things', 'invisible guess', 'demon slayer'];

      const apiKey = 'YOUR_API_KEY_HERE'; // Replace with your actual API key
      const baseURL = 'https://api.example.com/movies'; // Replace with the API endpoint

      try {
        const moviePromises = titles.map(async (title) => {
          const response = await fetch(`${baseURL}?title=${title}&apiKey=${apiKey}`);
          if (!response.ok) {
            throw new Error(`Error fetching ${title}`);
          }
          return response.json();
        });

        const moviesData = await Promise.all(moviePromises);
        setItems(moviesData);
      } catch (error) {
        console.error('Error fetching movies:', error);
        setItems([]); // Set items to an empty array in case of an error
      }
    };

    fetchMovies();
  }, []);

  return (
    <div>
      <Nav />
      <h1>Some of my favourite movies!</h1>
      <div>
        {items.map((movie, index) => (
          <div key={index} className="movie">
            <h2>{movie.title}</h2>
            <p>Release Year: {movie.releaseYear}</p> {/* Adjust based on your movie data */}
            <img
              src={movie.posterUrl || 'URL_TO_DEFAULT_POSTER_IMAGE_HERE'}
              alt={`Poster for ${movie.title}`}
              style={{ height: 400, width: 280 }}
              onError={(e) => {
                e.target.src = 'URL_TO_DEFAULT_POSTER_IMAGE_HERE';
              }}
            />
            <p>Director: {movie.director}</p> {/* Adjust based on your movie data */}
            <p>Plot: {movie.plot}</p> {/* Adjust based on your movie data */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default API;
