import React, { useState, useEffect } from 'react';

const HackerNewsItem = () => {
  const [story, setStory] = useState({});
  const [isLoading, setIsLoading] = useState(true);

  const fetchNewStory = () => {
    setIsLoading(true); // Set loading state to true before fetching
    fetch('https://hacker-news.firebaseio.com/v0/item/8863.json')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch story');
        }
        return response.json();
      })
      .then(data => {
        setStory(data);
        setIsLoading(false);
      })
      .catch(error => {
        console.error('Error fetching story:', error);
        setIsLoading(false);
      });
  };

  useEffect(() => {
    fetchNewStory(); // Fetch a story when the component mounts
  }, []);

  return (
    <div className="hacker-news-item">
      <h1>Hacker News Item</h1>
      <button onClick={fetchNewStory}>Get New Story</button>
      {isLoading ? (
        <p>Loading story...</p>
      ) : (
        <div className="story-details">
          <h2>{story.title}</h2>
          <p>Author: {story.by}</p>
          <p>Score: {story.score}</p>
          <p>URL: <a href={story.url} target="_blank" rel="noopener noreferrer">{story.url}</a></p>
        </div>
      )}
    </div>
  );
};

export default HackerNewsItem;
