import React, { useState, useEffect } from 'react';
import Nav from '../components/Nav'; // Replace this with your Navbar component

const API = () => {
  const [joke, setJoke] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetch('https://v2.jokeapi.dev/joke/Any?safe-mode')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch joke');
        }
        return response.json();
      })
      .then(data => {
        if (data.error) {
          throw new Error(data.error);
        }
        setJoke(data.joke || `${data.setup} ${data.delivery}`); // Adapt based on the API response structure
        setIsLoading(false);
      })
      .catch(error => {
        console.error('Error fetching joke:', error);
        setIsLoading(false);
      });
  }, []);

  return (
    <>
      <div className="API">
        <Nav /> 
        <h1>Random Joke</h1>
        {isLoading ? (
          <p>Loading joke...</p>
        ) : (
          <div className="joke-container">
            <p>{joke}</p>
          </div>
        )}
      </div>
    </>
  );
};

export default API;
